import React, { Component } from 'react';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import ClickOutside from './ClickOutside';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class App extends Component {
  state = {
      expanded: window.sidebarOpen
  }  

  render() {
    const datas = window.sidebarItems;  

    const navItems = datas ? datas.map(itemBar => {
        return (
            <NavItem eventKey={itemBar.id} key={itemBar.id} expanded={window.sidebarSelected.indexOf(itemBar.id+'/') !==-1} >
                { itemBar.icon ? (
                    <NavIcon>
                        <i className={itemBar.icon} style={{ fontSize: '1.75em' }} />
                    </NavIcon>
                  ) : (null)
                } 
                {  itemBar.text ? (
                    <NavText>
                        { itemBar.type === 'link' ? (
                                <a href={itemBar.link} ><b>{itemBar.text}</b></a>
                            ) : (<b>{itemBar.text}</b>)
                        }
                    </NavText> 
                  ) : (null)
                }   
                { itemBar.items ? (
                        itemBar.items.length ? (
                            itemBar.items.map((itemSub) => {
                                return (
                                    <NavItem eventKey={itemBar.id+'/'+itemSub.id} key={itemSub.id}>
                                        <NavText>
                                        {   itemSub.type === 'link' ? (
                                                <a href={itemSub.link} target={itemSub.target ? itemSub.target:''}>{itemSub.text}
                                                   <br /><small> { itemSub.summary ? ('( '+itemSub.summary+' )') : (null)}</small>
                                                </a>
                                            ) : (<b>{itemSub.text}</b>)
                                        }


                                        </NavText>        
                                    </NavItem>
                                )    
                            })

                       ) : (null) 
                    ) : (null)
                }    
            </NavItem>
        )   
    }): (
        <NavItem eventKey="no_data"></NavItem>
    )

    return (
      <div className="App">
        <ClickOutside
            onClickOutside={() => {
                this.setState({ expanded: false });
            }}
        >
            <SideNav
                className ="globalSidebar"
                expanded={this.state.expanded}
                onToggle={(expanded) => {
                    this.setState({ expanded });
                }}
            >
                <SideNav.Toggle />
                <SideNav.Nav defaultSelected={window.sidebarSelected}>
                    {navItems}
                </SideNav.Nav>
            </SideNav>
        </ClickOutside>
      </div>
    );
  }
}

export default App;
