import React, { Component } from 'react';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import ClickOutside from './ClickOutside';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class App extends Component {
  state = {
      expanded: true
  }  

  render() {
    const datas = [
        {id:'home', icon:"fa fa-fw fa-home", type:'link',link:'/', text:'Home'},
        {id:'node', icon:"fab fa-node-js", type:'text', text:'Node', items:[
            {id:'library', type:'link', text:'Library', summary:'Express-Node on Heroku-Mongose',link:'https://serene-everglades-58870.herokuapp.com/catalog', target:'_blank'},
            {id:'sppointments', type:'link', text:'Appointments', summary:'React-Node',link:'/appointments/'}
        ]},
        {id:'react', icon:"fab fa-react", type:'text', text:'React', items:[
            {id:'todos', type:'link', text:'Todos', summary:'React app', link:'/todos/'},
            {id:'multipage', type:'link', text:'React-Redux-Multipage', link:'/react-redux/'},
            {id:'marioplan', type:'link', text:'Marioplan', summary:'React-Redux-Firebase +cloud functions',link:'https://marioplan-45ff3.firebaseapp.com/', target:'_blank'},
        ]}
    ];  

    const navItems = datas ? datas.map(itemBar => {
        return (
            <NavItem eventKey={itemBar.id}>
                { itemBar.icon ? (
                    <NavIcon>
                        <i className={itemBar.icon} style={{ fontSize: '1.75em' }} />
                    </NavIcon>
                  ) : (null)
                } 
                {  itemBar.text ? (
                    <NavText>
                        { itemBar.type == 'link' ? (
                                <a href={itemBar.link}>{itemBar.text}</a>
                            ) : (itemBar.text)
                        }
                    </NavText> 
                  ) : (null)
                }   
                { itemBar.items ? (
                        itemBar.items.length ? (
                            itemBar.items.map((itemSub) => {
                                return (
                                    <NavItem eventKey={itemSub.id}>
                                        {   itemSub.type == 'link' ? (
                                                <a href={itemSub.link}>{itemSub.text}</a> 
                                            ) : (itemSub.text)
                                        }
                                    </NavItem>
                                )    
                            })

                       ) : (null) 
                    ) : (null)
                }    
            </NavItem>
        )   
    }): (
        <NavItem eventKey="no_data">Not Links Yet</NavItem>
    )
    // {navItems}
    return (
      <div className="App">
        <ClickOutside
            onClickOutside={() => {
                this.setState({ expanded: false });
            }}
        >
            <SideNav
                expanded={this.state.expanded}
                onToggle={(expanded) => {
                    this.setState({ expanded });
                }}
            >
                <SideNav.Toggle />
                <SideNav.Nav defaultSelected="home">
                    {navItems}
                </SideNav.Nav>
            </SideNav>
        </ClickOutside>
      </div>
    );
  }
}

export default App;
