import React, { Component } from 'react';
import SideNav, { Toggle, Nav, NavItem, NavIcon, NavText } from '@trendmicro/react-sidenav';
import ClickOutside from './ClickOutside';
import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class App extends Component {
  state = {
      expanded: true
  }  

  render() {
    const navItems = [
        {id:'home', icon:"fa fa-fw fa-home", type:'link',link:'/', text:'Home'},
        {id:'node', icon:"fab fa-node", type:'expandable', text:'Node', items:[
            {id:'library', type:'link', link:'https://serene-everglades-58870.herokuapp.com/catalog', target:'_blank'}
        ]},
        {id:'react', icon:"fab fa-react", type:'expandable', text:'React', items:[
            {id:'library', type:'link', link:'https://serene-everglades-58870.herokuapp.com/catalog', target:'_blank'}
        ]}
    ];  

    return (
      <div className="App">
        <ClickOutside
            onClickOutside={() => {
                this.setState({ expanded: false });
            }}
        >
            <SideNav
                expanded={this.state.expanded}
                onToggle={(expanded) => {
                    this.setState({ expanded });
                }}
            >
                <SideNav.Toggle />
                <SideNav.Nav defaultSelected="home">
                    <NavItem eventKey="home">
                        <NavIcon>
                            <i className="fa fa-fw fa-home" style={{ fontSize: '1.75em' }} />
                        </NavIcon>
                        <NavText>
                            <a href="/">Home</a>
                        </NavText>
                    </NavItem>

                    <NavItem eventKey="nodes">
                        <NavIcon>
                            <i className="fab fa-node" style={{ fontSize: '1.9em' }}/>   
                        </NavIcon>
                        <NavText>
                            Node
                        </NavText>
                        <NavItem eventKey="nodes/linenode">
                            <NavText>
                                Line Chart
                            </NavText>
                        </NavItem>
                        <NavItem eventKey="charts/barchart">
                            <NavText>
                                Bar Chart
                            </NavText>
                        </NavItem>
                    </NavItem>
                </SideNav.Nav>
            </SideNav>
        </ClickOutside>
      </div>
    );
  }
}

export default App;
